pragma solidity ^0.4.19;

contract Base {
    event PrintAddress(string _name, address _value);
    event PrintBytes32(string _name, bytes32 _value);
    event PrintUint16(string _name, uint16 _value);
    event PrintUint(string _name, uint _value);

}

contract Game is Base{

    //Created: the game created, but not confirmed openning to players;
    //Open: after owner create the game and User can bet;
    //GameEnd, game end and get the result
    //Close, the games is closed, and prize returned to winner;

    enum State {Created, Open, GameEnd, Close}

    address owner;

    //the cut owner can get is totalPrize * ownerCutMultiply / ownerCutDivide, in wei
    uint16 public ownerCutMultiply;

    uint16 public ownerCutDivide;

    uint public bidStartTime;

    uint public bidEndTime;

    //upperLimitInWei == 0 ? no limit
    struct Option {
        uint16 index;
        uint upperLimitInWei;
        uint16 oddsMultiply;
        uint16 oddsDivide;
        bool isValue;
    }

    mapping(uint16 => uint) public betAmounts;

    uint16[] public winOptionIndexs;

    struct Bet {
        address player;
        uint16 option;
        uint amount;// in wei
        bool win;
    }

    State public gameState;
    uint16[] public optionIndexs;
    //options of the game
    mapping(uint16 => Option) public options;

    Bet[] public bets;

    uint public finalTotalPrize = uint(0);

    modifier isOwner() {
        require(msg.sender == owner);
        _;
    }

    modifier onlyBefore(uint _time) { require(now < _time); _; }
    modifier onlyAfter(uint _time) { require(now > _time); _; }


    // the index must > 0
    function Game(address _owner, uint _bidStartTime, uint _bidEndTime, uint16 _ownerCutMultiply, uint16 _ownerCutDivide) public {
        require(_ownerCutMultiply < _ownerCutDivide);
        require(_bidEndTime > _bidStartTime);
        require(_bidStartTime >= now);
        owner = _owner;
        bidStartTime = _bidStartTime;
        bidEndTime = _bidEndTime;
        ownerCutMultiply = _ownerCutMultiply;
        ownerCutDivide = _ownerCutDivide;
        gameState = State.Created;
    }

    function addOptions(uint16 [] index, uint [] upperLimitInWei, uint16 [] oddsMultiply, uint16 [] oddsDivide) external isOwner {
        require(gameState == State.Created);
        require(index.length == upperLimitInWei.length);
        require(index.length == oddsMultiply.length);
        require(index.length == oddsDivide.length);
        for(uint8 i =0;i<index.length;i++) {
            require(index[i] > 0);
            Option memory o = Option(index[i], upperLimitInWei[i], oddsMultiply[i], oddsDivide[i], true);
            options[index[i]] = o;
            optionIndexs.push(index[i]);
        }
    }

    function openGame() external isOwner {
        require(gameState == State.Created);
        require(optionIndexs.length > 0);
        require(bidStartTime > 0);
        require(bidEndTime > 0);
        gameState = State.Open;
    }

    function payerBet(uint16 [] index, uint [] amount)  external payable onlyAfter(bidStartTime) onlyBefore(bidEndTime) {
        require(gameState == State.Open);
        require(checkOptionsValid(index));
        require(index.length == amount.length);
        require(uintArrayUtil.sum(amount) <= msg.value);
        require(indexAndUpperLimitCheck(index, amount));
        for(uint i=0;i<index.length;i++) {
            Bet memory b = Bet(msg.sender, index[i], amount[i], false);
            bets.push(b);
            betAmounts[index[i]] = betAmounts[index[i]] + amount[i];
        }
    }

    /**
     * returns > 0, need the owner transfer that amount of wei and the game can close;
     * returns = 0, no need for the owner to transfer
     *
     */
    function sendResult(uint16[] _winOptionIndexs) external isOwner onlyAfter(bidEndTime) returns(uint) {
        require(gameState == State.Open);
        require(_winOptionIndexs.length>0);
        require(checkOptionsValid(_winOptionIndexs));
        winOptionIndexs = _winOptionIndexs;
        gameState = State.GameEnd;
        uint totalPrize = uint(0);
        uint userPrize = uint(0);
        for(uint i=0;i<bets.length;i++) {
            Bet storage bet = bets[i];
            if(uintArrayUtil.contains(winOptionIndexs, bet.option)) {
                bet.win = true;
                userPrize = bet.amount * options[bet.option].oddsMultiply / options[bet.option].oddsDivide;
                userPrize = userPrize - userPrize * ownerCutMultiply / ownerCutDivide;
                totalPrize = totalPrize + userPrize;
            }
        }
        //substract the owner cut
        finalTotalPrize = totalPrize;
        if(finalTotalPrize > address(this).balance) {
            return finalTotalPrize - address(this).balance;
        } else {
            return uint(0);
        }
    }

    function queryAccountBalance() external view returns (uint balance) {
        return address(this).balance;
    }

    function closeGame() external payable isOwner {
        require(gameState == State.GameEnd);
        gameState = State.Close;
        require(finalTotalPrize <= address(this).balance);
        uint userPrize = uint(0);
        for(uint i=0;i<bets.length;i++) {
            Bet storage bet = bets[i];
            if(bet.win) {
                userPrize = bet.amount * options[bet.option].oddsMultiply / options[bet.option].oddsDivide;
                userPrize = userPrize - userPrize * ownerCutMultiply / ownerCutDivide;
                bet.player.transfer(userPrize);
            }
        }

        uint release = address(this).balance;
        if(release > 0) {
            owner.transfer(release);
        }
    }

    function CloseGameInForce() external isOwner {
        require(gameState != State.Close && gameState != State.Created);
        for(uint i=0;i<bets.length;i++) {
            Bet storage bet = bets[i];
            bet.player.transfer(bet.amount);
        }
        gameState = State.Close;
    }

    function checkOptionsValid(uint16[] _winOptionIndexs) internal view returns (bool){
        for(uint i=0;i<_winOptionIndexs.length;i++) {
            uint j = 0;
            while(j < optionIndexs.length) {
                if(optionIndexs[j] == _winOptionIndexs[i]){
                    break;
                }
                j++;
            }
            if(j >= optionIndexs.length) {
                return false;
            }
        }
        return true;
    }



    function indexAndUpperLimitCheck(uint16[] index, uint [] amount) internal view returns(bool){
        for(uint i=0;i<index.length;i++) {
            Option storage option = options[index[i]];
            if(!option.isValue)
                return false;
            if(option.upperLimitInWei != 0 && option.upperLimitInWei < betAmounts[index[i]] + amount[i]){
                return false;
            }
        }
        return true;

    }

    function () external payable {
    }

}


contract GameWorld is Base {
    uint16[] public gameIds;
    mapping(uint16 => address) public games;
    address public owner;
    event PrintUint(string name, uint value);

    modifier isOwner() {
        require(msg.sender == owner);
        _;
    }

    function GameWorld(address _owner) public {
        require(_owner != 0);
        owner = _owner;
    }

    function createGame(uint16 id, uint _bidStartTime, uint _bidEndTime, uint16 _ownerCutMultiply, uint16 _ownerCutDivide) external isOwner returns (address) {
        require(checkGameIdValid(id));
        address game = new Game(owner, _bidStartTime, _bidEndTime, _ownerCutMultiply, _ownerCutDivide);
        gameIds.push(id);
        games[id] = game;
        return game;
    }

    function getGame(uint16 id) external view returns (address) {
        address game = games[id];
        return game;
    }

    function checkGameIdValid(uint16 id) internal view returns (bool){
        uint i = 0;
        while(i<gameIds.length) {
            if(id == gameIds[i]) {
                break;
            }
            i++;
        }
        return i >= gameIds.length;
    }

}

library uintArrayUtil {

    function sum(uint [] array) public pure returns (uint){
        uint total;
        for(uint i=0;i<array.length;i++) {
            total = total + array[i];
        }
        return total;
    }

    function contains(uint16[] array, uint16 item) public pure returns (bool){
        for(uint i=0;i<array.length;i++) {
            if(array[i] == item) {
                return true;
            }
        }
        return false;
    }



}
